import React, { useState } from 'react'
import axios from 'axios';
export const EmojiContext= React.createContext();



const EmojiContextProvider = (props)=>{
    const [emoji,setEmoji] = useState("");

    const response=  axios.get(`https://http://localhost:3000/emoji`)
    .then(response=>setEmoji({emoji : response.data}))



    return(
        <EmojiContext.Provider value = {emoji} >
            {props.children}
        </EmojiContext.Provider>

    )




}

export default EmojiContextProvider;
