import React, { useState, useEffect, createContext } from 'react'
import axios from 'axios';
export const EmojiContext = createContext();



const EmojiContextProvider = (props) => {
    const [emoji, setEmoji] = useState([]);
    const [searchQuery, setSearchQuery] = useState("")


    const getData = async () => {
        await axios.get("http://localhost:3000/emoji")
            .then(response => {
                setEmoji(response.data)
            }
            )
    }

    useEffect(() => {

        getData();

    }, [])

    const searchEmoji = (e)=>{
        setSearchQuery({searchQuery:e.target.value})


    }

   let filteredEmoji = emoji.filter(
        (emo)=>{
            return  emo.title.indexOf(searchQuery) !== -1

        }

   )

   console.log(filteredEmoji)

   

   


    console.log(emoji)



    return (
        <EmojiContext.Provider value={{ filteredEmoji,searchEmoji }} >
            {props.children}
        </EmojiContext.Provider>

    )




}

export default EmojiContextProvider;
